import { PaperWallet } from '@thirdweb-dev/wallets';

const paperWallet = config => {
  const configuredWallet = {
    id: PaperWallet.id,
    meta: PaperWallet.meta,
    create: options => new PaperWallet({
      ...options,
      ...config
    }),
    config
  };
  return configuredWallet;
};

export { paperWallet };
